import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { toast } from 'react-toastify';
import axios from 'axios';
import { getError } from '../utils';

function Enable2FAScreen() {
  const [twoFAToken, setTwoFAToken] = useState('');

  const enable2FAHandler = async () => {
    try {
      // Отправьте запрос на сервер для включения 2FA
      const response = await axios.post('/api/users/enable-2fa', {
        email: 'user@example.com',
        password: 'user_password',
        token: twoFAToken,
      });

      // Обработайте успешный ответ
      toast.success('2FA enabled successfully');
      console.log(response.data);
    } catch (error) {
      // Обработайте ошибки
      toast.error(getError(error));
      console.error(error);
    }
  };

  return (
    <div className="container">
      <h2>Enable Two-Factor Authentication</h2>
      <Form>
        <Form.Group controlId="twoFAToken">
          <Form.Label>2FA Token</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter your 2FA token"
            value={twoFAToken}
            onChange={(e) => setTwoFAToken(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" onClick={enable2FAHandler}>
          Enable 2FA
        </Button>
      </Form>
    </div>
  );
}

export default Enable2FAScreen;
