import React, { useContext, useEffect, useReducer, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button, Container, Form, Table } from 'react-bootstrap';
import { Helmet } from 'react-helmet-async';
import { toast } from 'react-toastify';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { Store } from '../Store';
import { getError } from '../utils';

// Reducer для управления состоянием страницы
const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, loading: false, categories: action.payload };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function Categories() {
  const [{ loading, error, categories }, dispatch] = useReducer(reducer, {
    loading: true,
    categories: [],
    error: '',
  });

  const { state } = useContext(Store);
  const { userInfo } = state;

  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        dispatch({ type: 'FETCH_REQUEST' });
        const { data } = await axios.get('/api/categories', {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });
        dispatch({
          type: 'FETCH_SUCCESS',
          payload: data,
        });
      } catch (err) {
        dispatch({
          type: 'FETCH_FAIL',
          payload: getError(err),
        });
      }
    };
    fetchData();
  }, [userInfo]);

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      if (selectedCategory) {
        const { data } = await axios.put(
          `/api/categories/${selectedCategory._id}`,
          { name },
          {
            headers: { Authorization: `Bearer ${userInfo.token}` },
          }
        );
        toast.success('Category updated successfully');
        setSelectedCategory(null);
        setName('');
        dispatch({
          type: 'FETCH_SUCCESS',
          payload: categories.map((x) => (x._id === data._id ? data : x)),
        });
      } else {
        const { data } = await axios.post(
          '/api/categories',
          { name },
          {
            headers: { Authorization: `Bearer ${userInfo.token}` },
          }
        );
        toast.success('Category created successfully');
        setName('');
        dispatch({ type: 'FETCH_SUCCESS', payload: [...categories, data] });
      }
    } catch (error) {
      toast.error(getError(error));
    }
  };

  const editHandler = (category) => {
    setSelectedCategory(category);
    setName(category.name);
  };

  const cancelEditHandler = () => {
    setSelectedCategory(null);
    setName('');
  };

  const deleteHandler = async (category) => {
    if (window.confirm(`Are you sure to delete ${category.name}?`)) {
      try {
        await axios.delete(`/api/categories/${category._id}`, {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });
        toast.success('Category deleted successfully');
        dispatch({
          type: 'FETCH_SUCCESS',
          payload: categories.filter((x) => x._id !== category._id),
        });
      } catch (error) {
        toast.error(getError(error));
      }
    }
  };

  return (
    <Container className="small-container">
      <Helmet>
        <title>Categories</title>
      </Helmet>
      <div className="mb-4">
        <h1>Categories</h1>
      </div>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <>
          <Table striped bordered hover responsive className="table-sm mb-4">
            {/* ... table code ... */}
          </Table>
          <Form onSubmit={submitHandler} inline className="align-items-start">
            <Form.Control
              type="text"
              placeholder="Category Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="mr-2 mb-2"
            ></Form.Control>
            {selectedCategory ? (
              <>
                <Button type="submit" variant="primary" className="mr-2 mb-2">
                  Update Category
                </Button>
                <Button
                  variant="secondary"
                  onClick={cancelEditHandler}
                  className="mr-2 mb-2"
                >
                  Cancel
                </Button>
              </>
            ) : (
              <Button type="submit" variant="primary" className="mb-2">
                Create Category
              </Button>
            )}
          </Form>
        </>
      )}
    </Container>
  );
}

export default Categories;
