import React, { useContext, useEffect, useState } from 'react';
import { Helmet } from 'react-helmet-async';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal'; // Import Modal component
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { Store } from '../Store';
import { toast } from 'react-toastify';
import { getError } from '../utils';
import Axios from 'axios';

export default function SigninScreen() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [twoFactorToken, setTwoFactorToken] = useState('');
  const [show2FAModal, setShow2FAModal] = useState(false); // State for 2FA modal

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;

  const is2FAEnabled = userInfo && userInfo.enable2FA;

  const handleClose2FAModal = () => setShow2FAModal(false);
  const handleShow2FAModal = () => setShow2FAModal(true);

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const loginData = {
        email,
        password,
        twoFactorToken: is2FAEnabled ? twoFactorToken : undefined,
      };

      // If 2FA is enabled, show the 2FA modal
      if (is2FAEnabled) {
        handleShow2FAModal();
      } else {
        // Otherwise, proceed with regular sign in
        const { data } = await Axios.post('/api/users/signin', loginData);
        ctxDispatch({ type: 'USER_SIGNIN', payload: data });
        localStorage.setItem('userInfo', JSON.stringify(data));
        navigate(redirect || '/');
      }
    } catch (err) {
      toast.error(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        {is2FAEnabled && (
          <div className="mb-3">
            <Button variant="secondary" onClick={handleShow2FAModal}>
              Sign In with 2FA
            </Button>
          </div>
        )}
        <div className="mb-3">
          <Button type="submit">Sign In</Button>
        </div>
        <div className="mb-3">
          New customer?{' '}
          <Link to={`/signup?redirect=${redirect}`}>Create your account</Link>
        </div>
        <div className="mb-3">
          Forget Password? <Link to={`/forget-password`}>Reset Password</Link>
        </div>
      </Form>

      {/* 2FA Modal */}
      <Modal show={show2FAModal} onHide={handleClose2FAModal}>
        <Modal.Header closeButton>
          <Modal.Title>Two-Factor Authentication</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group controlId="twoFactorTokenModal">
            <Form.Label>2FA Token</Form.Label>
            <Form.Control
              type="text"
              required
              onChange={(e) => setTwoFactorToken(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose2FAModal}>
            Close
          </Button>
          <Button variant="primary" onClick={submitHandler}>
            Sign In
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
