import React, { useContext, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { Store } from '../Store';
import Axios from 'axios';
import { Helmet } from 'react-helmet-async';
import { toast } from 'react-toastify';
import { getError } from '../utils';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

export default function SigninScreen() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [twoFactorToken, setTwoFactorToken] = useState('');
  const [show2FAModal, setShow2FAModal] = useState(false);

  const { state, dispatch: ctxDispatch } = useContext(Store);

  const finalizeLogin = (data) => {
    ctxDispatch({ type: 'USER_SIGNIN', payload: data });
    localStorage.setItem('userInfo', JSON.stringify(data));
    navigate(redirect || '/');
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await Axios.post('/api/users/signin', { email, password });
      if (data.enable2FA) {
        setShow2FAModal(true);
      } else {
        finalizeLogin(data);
      }
    } catch (err) {
      toast.error(getError(err));
    }
  };

  const handle2FASubmission = async () => {
    try {
      const { data } = await Axios.post('/api/users/verify-2fa', {
        email,
        twoFactorToken
      });
      finalizeLogin(data);
      setShow2FAModal(false);
    } catch (err) {
      toast.error("Invalid 2FA code");
    }
  };

  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>
      <Form onSubmit={submitHandler}>
        // ... (ваш код для email и password fields)
        <div className="mb-3">
          <Button type="submit">Sign In</Button>
        </div>
        // ... (остальные ссылки)
      </Form>

      <Modal show={show2FAModal} onHide={() => setShow2FAModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Введите 2FA код</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group className="mb-3" controlId="twoFactorToken">
            <Form.Label>2FA Код</Form.Label>
            <Form.Control
              type="text"
              required
              onChange={(e) => setTwoFactorToken(e.target.value)}
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShow2FAModal(false)}>
            Закрыть
          </Button>
          <Button variant="primary" onClick={handle2FASubmission}>
            Войти
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
