import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios'; // Убедитесь, что axios установлен в вашем проекте

function Enable2FAScreen() {
    const [twoFAToken, setTwoFAToken] = useState('');

    const enable2FAHandler = async () => {
      try {
        // Отправьте запрос на сервер для включения 2FA
        const response = await axios.post('/api/users/enable-2fa', {
          email: 'user@example.com', // Замените на актуальный email пользователя
          password: 'user_password', // Замените на актуальный пароль пользователя
          token: twoFAToken,
        });

        // Обработайте успешный ответ
        console.log(response.data);
      } catch (error) {
        // Обработайте ошибки
        console.error(error);
      }
    };

    return (
      <div className="container">
        <h2>Enable Two-Factor Authentication</h2>
        <Form onSubmit={enable2FAHandler}>
          <Form.Group controlId="twoFAToken">
            <Form.Label>Two-Factor Authentication Token</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your 2FA token"
              value={twoFAToken}
              onChange={(e) => setTwoFAToken(e.target.value)}
              required
            />
          </Form.Group>
          <Button type="submit">Enable 2FA</Button>
        </Form>
      </div>
    );
  }

  export default Enable2FAScreen;
