import React, { useContext, useEffect, useReducer, useState } from 'react';

import { useNavigate, useParams } from 'react-router-dom';

import { toast } from 'react-toastify';

import axios from 'axios';

import { Store } from '../Store';

import { getError } from '../utils';

import Container from 'react-bootstrap/Container';

import Form from 'react-bootstrap/Form';

import { Helmet } from 'react-helmet-async';

import LoadingBox from '../components/LoadingBox';

import MessageBox from '../components/MessageBox';

import Button from 'react-bootstrap/Button';

import { getCategories, createCategory } from '../services/categoryService';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };

    case 'FETCH_SUCCESS':
      return { ...state, loading: false };

    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };

    case 'UPDATE_REQUEST':
      return { ...state, loadingUpdate: true };

    case 'UPDATE_SUCCESS':
      return { ...state, loadingUpdate: false };

    case 'UPDATE_FAIL':
      return { ...state, loadingUpdate: false };

    case 'UPLOAD_REQUEST':
      return { ...state, loadingUpload: true, errorUpload: '' };

    case 'UPLOAD_SUCCESS':
      return {
        ...state,

        loadingUpload: false,

        errorUpload: '',
      };

    case 'UPLOAD_FAIL':
      return { ...state, loadingUpload: false, errorUpload: action.payload };

    default:
      return state;
  }
};

export default function ProductEditScreen() {
  const navigate = useNavigate();

  const params = useParams(); // /product/:id

  const { id: productId } = params;

  const { state } = useContext(Store);

  const { userInfo } = state;

  const [{ loading, error, loadingUpdate, loadingUpload, errorUpload }, dispatch] = useReducer(reducer,
     {
      loading: true,
      error: '',
      loadingUpdate: false,
      loadingUpload: false,
      errorUpload: '',
    });

  const [name, setName] = useState('');

  const [slug, setSlug] = useState('');

  const [price, setPrice] = useState('');

  const [image, setImage] = useState('');

  const [images, setImages] = useState([]);

  const [category, setCategory] = useState('');

  const [countInStock, setCountInStock] = useState('');

  const [brand, setBrand] = useState('');

  const [description, setDescription] = useState('');

  const [categories, setCategories] = useState([]);

  const [newCategory, setNewCategory] = useState('');

  const [updatedCategory, setUpdatedCategory] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        dispatch({ type: 'FETCH_REQUEST' });

        const { data } = await axios.get(`/api/products/${productId}`);

        setName(data.name);

        setSlug(data.slug);

        setPrice(data.price);

        setImage(data.image);

        setImages(data.images);

        setCategory(data.category);

        setCountInStock(data.countInStock);

        setBrand(data.brand);

        setDescription(data.description);

        dispatch({ type: 'FETCH_SUCCESS' });
      } catch (err) {
        dispatch({
          type: 'FETCH_FAIL',

          payload: getError(err),
        });
      }
    };

    fetchData();
  }, [productId]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const data = await getCategories();

        setCategories(data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  const submitHandler = async (e) => {
    e.preventDefault();

    let updatedCategory = category;

    try {
      if (category === 'other') {
        const newCat = await createCategory(newCategory, userInfo.token);

        updatedCategory = newCat.name;
      }

      dispatch({ type: 'UPDATE_REQUEST' });

      console.log('Updated category:', updatedCategory); // Add this line for debugging

      await axios.put(
        `/api/products/${productId}`,

        {
          _id: productId,

          name,

          slug,

          price,

          image,

          images,

          category: updatedCategory,

          brand,

          countInStock,

          description,
        },

        {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        }
      );

      dispatch({
        type: 'UPDATE_SUCCESS',
      });

      toast.success('Product updated successfully');

      navigate('/admin/products');
    } catch (err) {
      toast.error(getError(err));

      dispatch({ type: 'UPDATE_FAIL' });
    }
  };

  const uploadFileHandler = async (e, forImages) => {
    const file = e.target.files[0];

    const bodyFormData = new FormData();

    bodyFormData.append('file', file);

    try {
      dispatch({ type: 'UPLOAD_REQUEST' });

      const { data } = await axios.post('/api/upload', bodyFormData, {
        headers: {
          'Content-Type': 'multipart/form-data',

          authorization: `Bearer ${userInfo.token}`,
        },
      });

      dispatch({ type: 'UPLOAD_SUCCESS' });

      if (forImages) {
        setImages([...images, data.path]);
    } else {
        setImage(data.path);
    }


      toast.success('Image uploaded successfully. click Update to apply it');
    } catch (err) {
      toast.error(getError(err));

      dispatch({ type: 'UPLOAD_FAIL', payload: getError(err) });
    }
  };

  const deleteFileHandler = async (fileName, f) => {
    console.log(fileName, f);

    console.log(images);

    console.log(images.filter((x) => x !== fileName));

    setImages(images.filter((x) => x !== fileName));

    toast.success('Image removed successfully. click Update to apply it');
  };

  return (
    <Container className="small-container">
      <Helmet>
        <title>Edit Product ${productId}</title>
      </Helmet>

      <h1>Edit Product {productId}</h1>

      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <Form onSubmit={submitHandler}>
          <Form.Group className="mb-3" controlId="name">
            <Form.Label>Name</Form.Label>

            <Form.Control
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="slug">
            <Form.Label>Slug</Form.Label>

            <Form.Control
              value={slug}
              onChange={(e) => setSlug(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="name">
            <Form.Label>Price</Form.Label>

            <Form.Control
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="image">
            <Form.Label>Image File</Form.Label>
            <Form.Control
              type="file"
              onChange={(e) => uploadFileHandler(e, false)}
            />
            {loadingUpload && <LoadingBox></LoadingBox>}
            {errorUpload && (
              <MessageBox variant="danger">{errorUpload}</MessageBox>
            )}
          </Form.Group>


          <Form.Group className="mb-3" controlId="category">
            <Form.Label>Category</Form.Label>

            <Form.Select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Select Category</option>

              {categories.map((cat) => (
                <option key={cat._id} value={cat.name}>
                  {cat.name}
                </option>
              ))}

              <option value="other">Other</option>
            </Form.Select>
          </Form.Group>

          {category === 'other' && (
            <Form.Group className="mb-3" controlId="newCategory">
              <Form.Label>New Category</Form.Label>

              <Form.Control
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
                placeholder="Enter new category"
                required
              />
            </Form.Group>
          )}

          <Form.Group className="mb-3" controlId="brand">
            <Form.Label>Brand</Form.Label>

            <Form.Control
              value={brand}
              onChange={(e) => setBrand(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="countInStock">
            <Form.Label>Count In Stock</Form.Label>

            <Form.Control
              value={countInStock}
              onChange={(e) => setCountInStock(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="description">
            <Form.Label>Description</Form.Label>

            <Form.Control
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </Form.Group>

          <div className="mb-3">
            <Button disabled={loadingUpdate} type="submit">
              Update
            </Button>

            {loadingUpdate && <LoadingBox></LoadingBox>}
          </div>
        </Form>
      )}
    </Container>
  );
}
